import React, { useState } from 'react';
import * as XLSX from 'xlsx';
import FileSaver from 'file-saver';

function ScheduleGenerator() {
  const [file, setFile] = useState(null);
  const [startDate, setStartDate] = useState('');
  const [loading, setLoading] = useState(false);

  const handleFileUpload = (e) => {
    const uploadedFile = e.target.files[0];
    setFile(uploadedFile);
  };

  const handleGenerateSchedule = async () => {
    if (!file || !startDate) {
      alert("請上傳 Excel 並輸入開始日期！");
      return;
    }

    setLoading(true);
    try {
      const data = await file.arrayBuffer();
      const workbook = XLSX.read(data);
      
      // 讀取必要的資料表
      const levelSheet = XLSX.utils.sheet_to_json(workbook.Sheets['level']);
      const refSheet = XLSX.utils.sheet_to_json(workbook.Sheets['ref_team']);

      // 創建新的工作簿
      const newWorkbook = XLSX.utils.book_new();
      
      // 添加分組資料
      const groupingSheet = XLSX.utils.json_to_sheet(levelSheet);
      XLSX.utils.book_append_sheet(newWorkbook, groupingSheet, "Groupings");

      // 添加裁判衝突表
      const refConflictSheet = XLSX.utils.json_to_sheet(refSheet);
      XLSX.utils.book_append_sheet(newWorkbook, refConflictSheet, "Referee Conflicts");

      // 輸出檔案
      const wbout = XLSX.write(newWorkbook, { type: "array", bookType: "xlsx" });
      FileSaver.saveAs(new Blob([wbout], { type: "application/octet-stream" }), "group_generate.xlsx");

      alert("分組和裁判衝突表已生成！請繼續上傳檔案進行排程。");
    } catch (error) {
      console.error("Error:", error);
      alert("生成過程中發生錯誤，請檢查檔案格式是否正確。");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-stripe font-sans">
      <div className="bg-white shadow-xl rounded-2xl p-8 w-full max-w-xl border-4 border-yellow-400 relative z-10">
        <h1 className="text-3xl font-extrabold text-blue-900 text-center mb-6">🏐 台大盃排球賽程排定</h1>

        <div className="mb-4">
          <label className="block text-blue-900 font-extrabold mb-2">📂 上傳 Excel 檔案：</label>
          <input
            type="file"
            accept=".xlsx"
            onChange={handleFileUpload}
            className="w-full p-2 border-2 border-yellow-300 rounded-md bg-blue-50"
          />
        </div>

        <div className="mb-4">
          <label className="block text-blue-900 font-extrabold mb-2">📅 輸入起始日期：</label>
          <input
            type="date"
            value={startDate}
            onChange={(e) => setStartDate(e.target.value)}
            className="w-full p-2 border-2 border-yellow-300 rounded-md bg-blue-50"
          />
        </div>

        <button
          onClick={handleGenerateSchedule}
          disabled={loading}
          className={`w-full py-3 px-4 bg-yellow-400 hover:bg-yellow-500 text-blue-900 font-bold rounded-lg transition duration-200 shadow-md ${
            loading ? 'opacity-50 cursor-not-allowed' : ''
          }`}
        >
          {loading ? '生成中...' : '生成分組和裁判衝突表'}
        </button>
      </div>
    </div>
  );
}

export default ScheduleGenerator;
