import React, { useState } from 'react';
import * as XLSX from 'xlsx';
import FileSaver from 'file-saver';

function ScheduleGenerator() {
  const [file, setFile] = useState(null);
  const [startDate, setStartDate] = useState('');
  const [loading, setLoading] = useState(false);

  const handleFileUpload = (e) => {
    const uploadedFile = e.target.files[0];
    setFile(uploadedFile);
  };

  const generateGroups = (levelSheet, refTeamSheet) => {
    // Êï¥ÁêÜÈöä‰ºçÁ≠âÁ¥öË≥áÊñô
    const teamLevels = {};
    levelSheet.forEach(row => {
      teamLevels[row.team_name] = row.level;
    });

    // ÊåâÁ≠âÁ¥öÂàÜÁµÑÈöä‰ºç
    const teamsByLevel = {1: [], 2: [], 3: [], 4: []};
    Object.entries(teamLevels).forEach(([team, level]) => {
      teamsByLevel[level].push(team);
    });

    // Ê™¢Êü•ÊØèÂÄãÁ≠âÁ¥öÁöÑÈöä‰ºçÊï∏Èáè
    const requiredTeamsPerLevel = 8;
    for (let level = 1; level <= 4; level++) {
      if (teamsByLevel[level].length < requiredTeamsPerLevel) {
        throw new Error(`Á≠âÁ¥ö ${level} ÁöÑÈöä‰ºçÊï∏Èáè‰∏çË∂≥ÔºöÈúÄË¶Å ${requiredTeamsPerLevel} ÈöäÔºå‰ΩÜÂè™Êúâ ${teamsByLevel[level].length} Èöä`);
      }
    }

    // Èö®Ê©üÂàÜÁµÑ
    const groupedTeams = {};
    const groups = ['A', 'B', 'C', 'D', 'E', 'F', 'G', 'H'];
    
    // ÁÇ∫ A-H ÁµÑÂàÜÈÖçÈöä‰ºç
    groups.forEach(group => {
      const selectedTeams = [];
      for (let level = 1; level <= 4; level++) {
        const randomIndex = Math.floor(Math.random() * teamsByLevel[level].length);
        const selectedTeam = teamsByLevel[level][randomIndex];
        selectedTeams.push(selectedTeam);
        teamsByLevel[level] = teamsByLevel[level].filter(team => team !== selectedTeam);
      }
      groupedTeams[group] = selectedTeams;
    });

    // ËôïÁêÜÂâ©È§òÈöä‰ºç
    const remainingTeams = [];
    for (let level = 1; level <= 4; level++) {
      remainingTeams.push(...teamsByLevel[level]);
    }

    // ÁÇ∫ I ÁµÑÈÅ∏ÊìáÈöä‰ºç
    if (remainingTeams.length >= 3) {
      const selectedTeams = [];
      const availableLevels = [1, 2, 3].filter(level => teamsByLevel[level].length > 0);
      
      if (availableLevels.length >= 3) {
        for (let i = 0; i < 3; i++) {
          const level = availableLevels[i];
          const randomIndex = Math.floor(Math.random() * teamsByLevel[level].length);
          const selectedTeam = teamsByLevel[level][randomIndex];
          selectedTeams.push(selectedTeam);
          teamsByLevel[level] = teamsByLevel[level].filter(team => team !== selectedTeam);
        }
      } else {
        // Â¶ÇÊûúÁÑ°Ê≥ïÂæû‰∏çÂêåÁ≠âÁ¥öÈÅ∏ÊìáÔºåÂâáÈö®Ê©üÈÅ∏Êìá3Èöä
        for (let i = 0; i < 3; i++) {
          const randomIndex = Math.floor(Math.random() * remainingTeams.length);
          selectedTeams.push(remainingTeams[randomIndex]);
          remainingTeams.splice(randomIndex, 1);
        }
      }
      groupedTeams['I'] = selectedTeams;
    }

    // Ââ©È§òÈöä‰ºçÊîæÂÖ• J ÁµÑ
    const finalRemainingTeams = [];
    for (let level = 1; level <= 4; level++) {
      finalRemainingTeams.push(...teamsByLevel[level]);
    }
    if (finalRemainingTeams.length > 0) {
      groupedTeams['J'] = finalRemainingTeams;
    }

    return groupedTeams;
  };

  const generateRefereeConflicts = (groupedTeams, refTeamSheet) => {
    const refConflicts = {};
    const referees = [...new Set(refTeamSheet.map(row => row.name))];

    referees.forEach(ref => {
      refConflicts[ref] = {};
      const conflictedTeams = new Set(
        refTeamSheet
          .filter(row => row.name === ref)
          .map(row => row.dept)
      );

      Object.entries(groupedTeams).forEach(([group, teams]) => {
        const hasConflict = teams.some(team => conflictedTeams.has(team));
        refConflicts[ref][group] = hasConflict ? 0 : 1;
      });
    });

    return refConflicts;
  };

  const handleGenerateSchedule = async () => {
    if (!file || !startDate) {
      alert("Ë´ã‰∏äÂÇ≥ Excel ‰∏¶Ëº∏ÂÖ•ÈñãÂßãÊó•ÊúüÔºÅ");
      return;
    }

    setLoading(true);
    try {
      const data = await file.arrayBuffer();
      const workbook = XLSX.read(data);
      
      // ËÆÄÂèñÂøÖË¶ÅÁöÑË≥áÊñôË°®
      const levelSheet = XLSX.utils.sheet_to_json(workbook.Sheets['level']);
      const refTeamSheet = XLSX.utils.sheet_to_json(workbook.Sheets['ref_team']);

      // ÁîüÊàêÂàÜÁµÑ
      const groupedTeams = generateGroups(levelSheet, refTeamSheet);
      
      // ÁîüÊàêË£ÅÂà§Ë°ùÁ™ÅË°®
      const refConflicts = generateRefereeConflicts(groupedTeams, refTeamSheet);

      // ÂâµÂª∫ÂàÜÁµÑË≥áÊñô
      const groupingData = [];
      Object.entries(groupedTeams).forEach(([group, teams]) => {
        teams.forEach(team => {
          groupingData.push({
            Group: group,
            Team: team,
            Level: levelSheet.find(row => row.team_name === team).level
          });
        });
      });

      // ÂâµÂª∫Êñ∞ÁöÑÂ∑•‰ΩúÁ∞ø
      const newWorkbook = XLSX.utils.book_new();
      
      // Ê∑ªÂä†ÂàÜÁµÑË≥áÊñô
      const groupingSheet = XLSX.utils.json_to_sheet(groupingData);
      XLSX.utils.book_append_sheet(newWorkbook, groupingSheet, "Groupings");

      // Ê∑ªÂä†Ë£ÅÂà§Ë°ùÁ™ÅË°®
      const refConflictData = Object.entries(refConflicts).map(([ref, conflicts]) => ({
        Referee: ref,
        ...conflicts
      }));
      const refConflictSheet = XLSX.utils.json_to_sheet(refConflictData);
      XLSX.utils.book_append_sheet(newWorkbook, refConflictSheet, "Referee Conflicts");

      // Ëº∏Âá∫Ê™îÊ°à
      const wbout = XLSX.write(newWorkbook, { type: "array", bookType: "xlsx" });
      FileSaver.saveAs(new Blob([wbout], { type: "application/octet-stream" }), "group_generate.xlsx");

      alert("ÂàÜÁµÑÂíåË£ÅÂà§Ë°ùÁ™ÅË°®Â∑≤ÁîüÊàêÔºÅË´ãÁπºÁ∫å‰∏äÂÇ≥Ê™îÊ°àÈÄ≤Ë°åÊéíÁ®ã„ÄÇ");
    } catch (error) {
      console.error("Error:", error);
      alert(error.message || "ÁîüÊàêÈÅéÁ®ã‰∏≠ÁôºÁîüÈåØË™§ÔºåË´ãÊ™¢Êü•Ê™îÊ°àÊ†ºÂºèÊòØÂê¶Ê≠£Á¢∫„ÄÇ");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-stripe font-sans">
      <div className="bg-white shadow-xl rounded-2xl p-8 w-full max-w-xl border-4 border-yellow-400 relative z-10">
        <h1 className="text-3xl font-extrabold text-blue-900 text-center mb-6">üèê Âè∞Â§ßÁõÉÊéíÁêÉË≥ΩÁ®ãÊéíÂÆö</h1>

        <div className="mb-4">
          <label className="block text-blue-900 font-extrabold mb-2">üìÇ ‰∏äÂÇ≥ Excel Ê™îÊ°àÔºö</label>
          <input
            type="file"
            accept=".xlsx"
            onChange={handleFileUpload}
            className="w-full p-2 border-2 border-yellow-300 rounded-md bg-blue-50"
          />
        </div>

        <div className="mb-4">
          <label className="block text-blue-900 font-extrabold mb-2">üìÖ Ëº∏ÂÖ•Ëµ∑ÂßãÊó•ÊúüÔºö</label>
          <input
            type="date"
            value={startDate}
            onChange={(e) => setStartDate(e.target.value)}
            className="w-full p-2 border-2 border-yellow-300 rounded-md bg-blue-50"
          />
        </div>

        <button
          onClick={handleGenerateSchedule}
          disabled={loading}
          className={`w-full py-3 px-4 bg-yellow-400 hover:bg-yellow-500 text-blue-900 font-bold rounded-lg transition duration-200 shadow-md ${
            loading ? 'opacity-50 cursor-not-allowed' : ''
          }`}
        >
          {loading ? 'ÁîüÊàê‰∏≠...' : 'ÁîüÊàêÂàÜÁµÑÂíåË£ÅÂà§Ë°ùÁ™ÅË°®'}
        </button>
      </div>
    </div>
  );
}

export default ScheduleGenerator;
