import React, { useState } from 'react';
import * as XLSX from 'xlsx';
import FileSaver from 'file-saver';
import { useNavigate } from 'react-router-dom';

function ScheduleGenerator() {
  const [groupFile, setGroupFile] = useState(null);
  const [availabilityFile, setAvailabilityFile] = useState(null);
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleGroupFileUpload = (e) => {
    const uploadedFile = e.target.files[0];
    setGroupFile(uploadedFile);
  };

  const handleAvailabilityFileUpload = (e) => {
    const uploadedFile = e.target.files[0];
    setAvailabilityFile(uploadedFile);
  };

  const handleGenerateSchedule = async () => {
    if (!groupFile || !availabilityFile) {
      alert("請上傳分組檔案和可用性檔案！");
      return;
    }

    setLoading(true);
    try {
      const formData = new FormData();
      formData.append('group_file', groupFile);
      formData.append('availability_file', availabilityFile);

      const response = await fetch('/api/generate-schedule', {
        method: 'POST',
        body: formData
      });

      const result = await response.json();
      if (result.error) {
        alert(result.error);
        setLoading(false);
        return;
      }
      // 建立 Excel 檔案
      const workbook = XLSX.utils.book_new();
      const scheduleSheet = XLSX.utils.json_to_sheet(result.schedule);
      XLSX.utils.book_append_sheet(workbook, scheduleSheet, "Schedule");
      const refCountSheet = XLSX.utils.json_to_sheet(result.referee_counts);
      XLSX.utils.book_append_sheet(workbook, refCountSheet, "Referee Counts");
      // 下載到本地
      const wbout = XLSX.write(workbook, { type: "array", bookType: "xlsx" });
      FileSaver.saveAs(new Blob([wbout], { type: "application/octet-stream" }), "schedule.xlsx");
      // 同時將檔案上傳到 public 目錄（需後端支援，這裡假設有 /api/upload-schedule）
      const uploadForm = new FormData();
      uploadForm.append('file', new Blob([wbout], { type: "application/octet-stream" }), 'schedule.xlsx');
      await fetch('/api/upload-schedule', { method: 'POST', body: uploadForm });
      // 跳轉到賽程瀏覽頁
      navigate('/schedule-viewer');
    } catch (error) {
      alert(error.message || "生成過程中發生錯誤，請檢查檔案格式是否正確。");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-stripe font-sans">
      <div className="bg-white shadow-xl rounded-2xl p-8 w-full max-w-xl border-4 border-yellow-400 relative z-10">
        <h1 className="text-3xl font-extrabold text-blue-900 text-center mb-6">🏐 台大盃排球賽程生成器</h1>
        <div className="mb-4">
          <label className="block text-blue-900 font-extrabold mb-2">📂 上傳分組檔案：</label>
          <input type="file" accept=".xlsx" onChange={handleGroupFileUpload} className="w-full p-2 border-2 border-yellow-300 rounded-md bg-blue-50" />
        </div>
        <div className="mb-4">
          <label className="block text-blue-900 font-extrabold mb-2">📂 上傳可用性檔案：</label>
          <input type="file" accept=".xlsx" onChange={handleAvailabilityFileUpload} className="w-full p-2 border-2 border-yellow-300 rounded-md bg-blue-50" />
        </div>
        <button onClick={handleGenerateSchedule} disabled={loading} className={`w-full py-3 px-4 bg-yellow-400 hover:bg-yellow-500 text-blue-900 font-bold rounded-lg transition duration-200 shadow-md ${loading ? 'opacity-50 cursor-not-allowed' : ''}`}>{loading ? '生成中...' : '生成賽程'}</button>
      </div>
    </div>
  );
}

export default ScheduleGenerator;
