import React, { useState } from 'react';
import * as XLSX from 'xlsx';
import FileSaver from 'file-saver';
import { BrowserRouter as Router, Routes, Route, useNavigate } from 'react-router-dom';
import ScheduleGenerator from './ScheduleGenerator';

function Home() {
  const [file, setFile] = useState(null);
  const [startDate, setStartDate] = useState('');
  const navigate = useNavigate();

  const handleFileUpload = (e) => {
    const uploadedFile = e.target.files[0];
    setFile(uploadedFile);
  };

  const handleGenerateSchedule = async () => {
    if (!file || !startDate) {
      alert("請上傳 Excel 並輸入開始日期！");
      return;
    }

    const data = await file.arrayBuffer();
    const workbook = XLSX.read(data);
    const levelSheet = XLSX.utils.sheet_to_json(workbook.Sheets['level']);
    const refSheet = XLSX.utils.sheet_to_json(workbook.Sheets['ref_team']);

    // 示例假資料
    const generatedGroups = [
      { Group: 'A', Team: 'Team1', Level: 1 },
      { Group: 'A', Team: 'Team2', Level: 2 },
      { Group: 'B', Team: 'Team3', Level: 1 },
      { Group: 'B', Team: 'Team4', Level: 2 },
    ];

    const schedule = generateSchedule(generatedGroups, startDate);

    const ws = XLSX.utils.json_to_sheet(schedule);
    const wb = XLSX.utils.book_new();
    XLSX.utils.book_append_sheet(wb, ws, "Schedule");

    const wbout = XLSX.write(wb, { type: "array", bookType: "xlsx" });
    FileSaver.saveAs(new Blob([wbout], { type: "application/octet-stream" }), "schedule.xlsx");

    // 生成完賽程後跳轉到排程頁面
    navigate('/schedule-generator');
  };

  const generateSchedule = (groupedData, startDate) => {
    const schedule = [];
    const baseDate = new Date(startDate);
    const groupGames = {};

    groupedData.forEach(row => {
      const group = row.Group;
      if (!groupGames[group]) groupGames[group] = [];
      groupGames[group].push(row.Team);
    });

    let currentDate = new Date(baseDate);
    Object.entries(groupGames).forEach(([group, teams]) => {
      const matches = [];
      for (let i = 0; i < teams.length; i++) {
        for (let j = i + 1; j < teams.length; j++) {
          matches.push({ group, teamA: teams[i], teamB: teams[j] });
        }
      }
      matches.forEach(match => {
        schedule.push({
          Date: currentDate.toISOString().split('T')[0],
          Group: match.group,
          Match: `${match.teamA} vs ${match.teamB}`
        });
        currentDate.setDate(currentDate.getDate() + 1);
      });
    });

    return schedule;
  };

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-stripe font-sans">
      {/* 左上角單一排球 Emoji */}


      {/* 前景內容 */}
      <div className="bg-white shadow-xl rounded-2xl p-8 w-full max-w-xl border-4 border-yellow-400 relative z-10">
        <h1 className="text-3xl font-extrabold text-blue-900 text-center mb-6">🏐 台大盃排球賽程生成器</h1>

        <div className="mb-4">
          <label className="block text-blue-900 font-extrabold mb-2">📂 上傳 Excel 檔案：</label>
          <input
            type="file"
            accept=".xlsx"
            onChange={handleFileUpload}
            className="w-full p-2 border-2 border-yellow-300 rounded-md bg-blue-50"
          />
        </div>

        <div className="mb-4">
          <label className="block text-blue-900 font-extrabold mb-2">📅 輸入起始日期：</label>
          <input
            type="date"
            value={startDate}
            onChange={(e) => setStartDate(e.target.value)}
            className="w-full p-2 border-2 border-yellow-300 rounded-md bg-blue-50"
          />
        </div>

        <button
          onClick={handleGenerateSchedule}
          className="w-full py-3 px-4 bg-yellow-400 hover:bg-yellow-500 text-blue-900 font-bold rounded-lg transition duration-200 shadow-md"
        >
          生成賽程 Excel
        </button>
      </div>
    </div>
  );
}

function App() {
  return (
    <Router>
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/schedule-generator" element={<ScheduleGenerator />} />
      </Routes>
    </Router>
  );
}

export default App;
